
public class Oddeven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] numbers= {2,34,453,5,343,322,4234,34534,53452312,2,212112,1223,3};
		for(int i=0; i<numbers.length;i++)
		{
		  if (numbers[i] % 2 == 0)
		  {
		  System.out.println("Given no. is even "+ numbers[i]);
		  }
		  else
		  {
		  System.out.println("Given no. is odd "+ numbers[i]);
				    
			  
		  }
		  
			
			
		}
		
	}

}
